import React from 'react'
import { Link } from 'react-router-dom';

function scrollAction(){
 <a href="#artist-info-section"></a>

}


const Form = props => (

  <form onSubmit={props.getArtist} style={{ marginBottom:"2rem" }}>
      <input className="form__input" type="text" name="artistName" required/>
      <button onClick={ scrollAction} className="form__button get-started-btn">Search</button>

  </form>
);

export default Form;
