import React from 'react';

const API_ID = "codingbootcamp";

class Events extends React.Component{

    state = {
        events: []
    }
    componentDidMount = async () =>{
        const title = this.props.location.state.artist;
        console.log(title);
        const req = await fetch
        (`https://rest.bandsintown.com/artists/${title}/events/?app_id=${API_ID}`); 
        const res = await req.json();
        this.setState({events: res})
        console.log(this.state.events)

        // fetch(`https://rest.bandsintown.com/artists/${title}/events/?app_id=${API_ID}`)
        // .then(response => response.json())
        // .then(({results:events}) => this.setState({events}))




    }





    render(){
        const events = this.state.events;
        // console.log(event);
        // console.log("Length: " + event.length);


        return ( 
            <section id="steps" class="steps">
            <div className="event-info-display col-lg-12 col-md-6 content-item" data-aos="fade-up" data-aos-delay="100">

               {events.map(event => 
            //    <div id="final-events-display" className="row no-gutters" data-aos="fade-up">
                    // <div className="event-info-display col-lg-12 col-md-6 content-item" data-aos="fade-up" data-aos-delay="100">
                    <div className="event-card">
                        <h4 className="event-heading">Event Details</h4>
                        <ul className="event-details-list">
                            <li className="event-country"><strong>Country:</strong> {event.venue.country}</li>
                            <li className="event-city"><strong>City:</strong> {event.venue.city}</li>
                            <li className="event-venue"><strong>Venue:</strong> {event.venue.name}</li>
                            <li className="event-date"><strong>Date:</strong> {event.venue.datetime}</li>

                        </ul>

                    </div>


               )}
            </div>
            </section>

        );



    }

}

export default Events;
