import React from 'react';
// import Events from '../pages/Events.js'
import { Link } from "react-router-dom";

class ArtistInfo extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            artists : []
        }

    }



    render(){

        return(


            <section className="artist-info" id="artist-info-section">
            <section id="hero" className="d-flex align-items-center">
                <div className="artist-info-display container d-flex flex-column align-items-center justify-content-center" data-aos="fade-up">

                    <h1 className = "artist-name">{this.props.artists.name}</h1>
                    <img src={artists.image_url} className="artist-image hero-img" alt={artists.name} data-aos="" data-aos-delay="150"/>

                    <button id= "fb-button"><a className="fb-link" href={artists.facebook_page_url} target="_blank">Visit Facebook Profile</a></button> 
                    {/* <button id= "events-button" ><a className="events-link">View Events</a></button>  */}

                    <button id="events-button"> 
                    <Link  to={{ 

                        pathname: `/${artists.name}/events`,
                        state: { artist: artists.name }
                        }}>View Events</Link>

                    </button>

                </div>
            </section>
        </section>


        );



    }


}








export default ArtistInfo;
